mapper.select:
SELECT * FROM cart, commodity_specs, commodity, img WHERE img.img_type = '商品图' AND img_belong = commodity.commodity_id AND img_order = 1 AND cart_status != 0 AND cart_status != 5 AND commodity_specs.commodity_id = commodity.commodity_id AND cart.user_id = #{user_id} AND cart.commodity_specs_id = commodity_specs.commodity_specs_id AND cart.commodity_specs_id IN ( # 这里不能写collection=list
#{commodity_specs_id}
)

SELECT * FROM orders WHERE user_id = #{user_id} AND order_status != 0

     AND order_id = #{order_id}

 ORDER BY order_create_time DESC;

     SELECT t2.* FROM ORDERS t1,order_detail t2
 WHERE t1.ORDER_ID = t2.ORDER_ID AND t1.ORDER_ID = #{order_id}
AND t1.ORDER_STATUS != 0 AND t2.order_detail_status !=0;

mapper.update:
UPDATE orders
SET order_pay_type = #{pay_type},
    order_status   = 1,
    order_logistics='未发货'
WHERE user_id = #{user_id}
  AND order_id = #{order_id}

mapper.select:
SELECT sum(cart.commodity_count) sum_commodity_count, sum(cart.commodity_count * commodity_specs.commodity_specs_present_price) sum_commodity_present_price, sum(cart.commodity_count * commodity_specs.commodity_specs_present_price) + #{order_freight_price} sum_commodity_pay_price FROM commodity_specs, cart WHERE commodity_specs.commodity_specs_id = cart.commodity_specs_id AND cart_status != 5 AND cart_status != 0 AND user_id = #{user_id} AND cart.commodity_specs_id IN(
#{commodity_specs_id}
) /* 物流价格是从queryOrderFreightPrice 中查询到的，所以安全 */
